#include <iostream>
#include <set>
#include <vector>
#include <string>
#include <algorithm>
using namespace std;
class Solution {
public:
    int num = 0;
    int nowIndex = 0;
    int wordsLength;
    vector<int> findSubstring(string s, vector<string>& words) {
        bool sign[5000] = {false};
        set<int> noRepeat;
        vector<int> result;
        num = words.size();
        if(num > s.size()) {
            return result;
        }
        sort(words.begin(), words.end());
        wordsLength = words.size();
        int length;
        length = words[0].size();
        bool match = false;
        vector<vector<int>> index(num);
        for(int i = 0; i < num; i++) {
            vector<int> tmp;
            int t = 0;
            while((t = s.find(words[i], t)) >= 0) {
                tmp.push_back(t);
                t += 1;
            }
            if(tmp.size() == 0) {
                return tmp;
            }
            index[i] = tmp;
            while(i+1<num&&words[i + 1] == words[i]) {
                i++;
                index[i] = tmp;
            }
            if(tmp.size() * length == s.size()) {
                match = true;
                for(int i = 1; i <tmp.size(); i++) {
                    if(tmp[i] != tmp[i-1] + length) {
                        match = false;
                        break;
                    }
                }
                
            }
        }
        if(match && index[wordsLength - 1] != index[0]) {
            return result;
        }
        for(int i = 0; i < index.size(); i++) {
            if(!sign[i]) {
                sign[i] = true;
                num = words.size() - 1;
                for(int j = 0; j < index[i].size(); j++) {     
                    nowIndex =  index[i][j] + length;
                    if(s.size() - nowIndex < num * length) {
                        break;
                    }
                    if(num == 0||seek(length,sign, index)) {
                        noRepeat.insert(index[i][j]);
                    }
                }
                sign[i] = false;
            }
            while(i+1 < words.size()&&words[i + 1] == words[i]) {
                i++;
            }
        }
        result.assign(noRepeat.begin(), noRepeat.end());
        return result;
    }

    bool seek(int length, bool* sign,  vector<vector<int>>& index) {
        for(int i = 0; i < wordsLength; i++) {
            if(!sign[i])  {
                sign[i] = true;
                for(int j = 0; j < index[i].size(); j++) {
                    if(nowIndex >  index[i][j]) {
                        continue;
                    } else if(nowIndex < index[i][j]) {
                        break;
                    }
                    num--;
                    nowIndex += length;
                    if(num == 0 || seek(length,sign, index)) {
                        num++;
                        sign[i] = false;
                        return true;
                    }
                    //认真一点，写仔细
                    nowIndex -= length;
                    num++;
                }
                sign[i] = false;
                if(index[wordsLength - 1] == index[i]) {
                    break;
                }
                while(i+1 < wordsLength&&index[i+1] == index[i]) {
                    i++;
                }
            }
        }
        return false;
    }
};

int main() {
    Solution so;
    string s = "  aba   babababababab";
    vector<string> words({"ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba"});
     for(auto t : so.findSubstring(s, words)) {
        cout << t << " ";    
     }
     cout << endl;
    return 0;
}